import cookie from 'js-cookie';
import { productConstants } from '../redux-constants/product-constant';
import { addProductCategoryService } from '../redux-helpers/product-helper';
import { showWarning } from '../fragments-tools/alert-toasts';
export function addProductCategory(name, description, close) {
  return function (dispatch) {
    addProductCategoryService(name, description).then(function (response) {
      return response;
    }).then(function (second) {
      if (second.hasOwnProperty('errors')) {
        showWarning("Addition catégorie échoué.");
      } else {
        cookie.set('product-categories', JSON.stringify(second.data.addProductCategoryQuery));
        close(false);
      }
    });
  };

  function request() {
    return {
      type: productConstants.productCategoryAddRequest,
      categories: null
    };
  }

  function success(categories) {
    return {
      type: productConstants.productCategoryAddSuccess,
      categories: categories
    };
  }

  function failure() {
    return {
      type: productConstants.productCategoryAddFailure,
      categories: null
    };
  }
}
